mapper.select:
select

from data_stream

    is_deleted = 0

        and inlong_group_id = #{groupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

select count(1)
from data_stream
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0

select distinct ds.id, ds.inlong_group_id, ds.inlong_stream_id, ds.name, ds.description, ds.mq_resource_obj, ds.data_source_type, ds.storage_period, ds.data_type, ds.data_encoding, ds.data_separator, ds.data_escape_char, ds.have_predefined_fields, ds.daily_records, ds.daily_storage, ds.peak_records, ds.max_length, ds.in_charges, ds.status, ds.creator, ds.modifier, ds.create_time, ds.modify_time from data_stream ds, business biz, wf_approver approver
ds.is_deleted = 0 and approver.process_name = "NEW_BUSINESS_WORKFLOW" and approver.is_deleted = 0 and ds.inlong_group_id = biz.inlong_group_id and biz.is_deleted = 0
and ds.inlong_group_id = #{request.inlongGroupId, jdbcType=VARCHAR}
and ( find_in_set(#{request.currentUser, jdbcType=VARCHAR}, biz.in_charges) or find_in_set(#{request.currentUser, jdbcType=VARCHAR}, biz.followers) or find_in_set(#{request.currentUser, jdbcType=VARCHAR}, approver.approvers) )
and ds.data_source_type = #{request.dataSourceType, jdbcType=VARCHAR}
and (ds.inlong_stream_id like CONCAT('%', #{request.keyWord}, '%') or ds.name like CONCAT('%', #{request.keyWord}, '%') or ds.description like CONCAT('%', #{request.keyWord}, '%') )
and ds.status = #{request.status, jdbcType=INTEGER}
order by ds.modify_time desc

mapper.update:
update data_stream
set status = #{status, jdbcType=INTEGER}, modifier = #{modifier, jdbcType=VARCHAR}

    inlong_group_id = #{groupId, jdbcType=VARCHAR}

        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

    and is_deleted = 0

update data_stream
set is_deleted = 1,
    modifier   = #{operator, jdbcType=VARCHAR}
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0
