mapper.select:
select

from data_stream_ext
where business_identifier = #{bid, jdbcType=VARCHAR}
and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
and is_deleted = 0

select

from data_stream_ext
where business_identifier = #{bid, jdbcType=VARCHAR}
and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
and key_name = #{keyName, jdbcType=VARCHAR}
and is_deleted = 0

mapper.delete:
delete
from data_stream_ext
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0

mapper.update:
update data_stream_ext
set is_deleted = 1
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0
