mapper.select:
select inlong_group_id
from inlong_group
where proxy_cluster_id = #{proxyClusterId,jdbcType=INTEGER}

mapper.update:
update inlong_group
set status   = #{status,jdbcType=INTEGER},
    modifier = #{modifier,jdbcType=VARCHAR}
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and is_deleted = 0
