mapper.select:
select

from source_db_basic
where business_identifier = #{bid, jdbcType=VARCHAR}
and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
and is_deleted = 0

mapper.delete:
delete
from source_db_basic
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0

mapper.update:
update source_db_basic
set is_deleted = 1,
    modifier   = #{operator,jdbcType=VARCHAR}
where business_identifier = #{bid, jdbcType=VARCHAR}
  and data_stream_identifier = #{dsid, jdbcType=VARCHAR}
  and is_deleted = 0
